definition.script:
pipeline {
  environment {
    registry = "d0ckerprodigy/spreeproject"
    registryCredential = 'dockerhub'
    dockerImage = ''
  }
  agent any
  stages {
    stage('Cloning Git Repo') {
      steps {
        git 'https://github.com/L1nuxProdigy/spreeLive'
      }
    }
    stage('Building image') {
      steps{
        script {
          dockerImage = docker.build registry + ":$BUILD_NUMBER"
        }
      }
    }
    stage('Deploy Image') {
      steps{
        script {
          docker.withRegistry( '', registryCredential ) {
            dockerImage.push()
          }
        }
      }
    }
    stage('Remove Unused docker image') {
      steps{
        sh "docker rmi $registry:$BUILD_NUMBER"
      }
    }
        stage ('Apply to K8s') {
      steps{
        sshagent(credentials : ['a9e580a1-1d3e-4e8a-929c-4f07a36ae54a']) {
          sh 'ssh -o StrictHostKeyChecking=no ubuntu@Change_Me_to_IP \'sed -i "s/spreeproject[^ ]*/spreeproject:\'$BUILD_NUMBER\'/g" finalProject-OpsSchool/kubernetes/k8s_deployment.yml\''
          sh 'ssh -o StrictHostKeyChecking=no ubuntu@Change_Me_to_IP \'kubectl apply -f /home/ubuntu/finalProject-OpsSchool/kubernetes/k8s_deployment.yml\''
        }
      }
        }
  }
}
